import React from "react";
// react plugin for creating charts
import ChartistGraph from "react-chartist";
// @material-ui/core
import { makeStyles } from "@material-ui/core/styles";
import Icon from "@material-ui/core/Icon";
// @material-ui/icons
// import Warning from "@material-ui/icons/Warning";
// import DateRange from "@material-ui/icons/DateRange";
// import LocalOffer from "@material-ui/icons/LocalOffer";
import Update from "@material-ui/icons/Update";
import ArrowUpward from "@material-ui/icons/ArrowUpward";
import AccessTime from "@material-ui/icons/AccessTime";
// import BugReport from "@material-ui/icons/BugReport";
// import Code from "@material-ui/icons/Code";
// import Cloud from "@material-ui/icons/Cloud";
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Table from "components/Table/Table.js";
// import Tasks from "components/Tasks/Tasks.js";
// import CustomTabs from "components/CustomTabs/CustomTabs.js";
// import Danger from "components/Typography/Danger.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardIcon from "components/Card/CardIcon.js";
import CardBody from "components/Card/CardBody.js";
import CardFooter from "components/Card/CardFooter.js";

// import { bugs, website, server } from "variables/general.js";

// import {
//   dailySalesChart,
//   emailsSubscriptionChart,
//   completedTasksChart,
// } from "variables/charts.js";
import {
  dailySalesChartAmazon,
  dailySalesChartApple,
  dailySalesChartAlphbet,
} from "variables/charts.js";

import styles from "assets/jss/material-dashboard-react/views/dashboardStyle.js";

const useStyles = makeStyles(styles);

const watson_script =
  "window.watsonAssistantChatOptions = {" +
  "integrationID: 'c947c3a0-7a64-4fe7-841f-817ee2d4b2b7'," +
  "region: 'eu-gb'," +
  "serviceInstanceID: '0b081cf6-7d42-45fb-937f-c507cc976130'," +
  "onLoad: function(instance) { instance.render(); }" +
  "};" +
  "setTimeout(function(){" +
  "const t=document.createElement('script');" +
  "t.src='https://web-chat.global.assistant.watson.appdomain.cloud/versions/' + (window.watsonAssistantChatOptions.clientVersion || 'latest') + '/WatsonAssistantChatEntry.js';" +
  "document.head.appendChild(t);" +
  "});";

export default function Dashboard() {
  // const search = window.location.search;
  // const params = new URLSearchParams(search);
  // const industry = params.get("industry");
  // const volatility = params.get("volatility");
  // const restriction = params.get("restriction");
  // const investment = params.get("investment");

  // const [data, setData] = React.useState(null);
  // fetching the GET route from the Express server which matches the GET route from server.js
  // const retrieveStockList = async () => {
  //   const stock_list_url ="/stock_list?industry="
  //     +industry+"&volatility="+volatility+"&restriction="+restriction;
  //   const stock_list_response = await fetch(stock_list_url);
  //   const body = await stock_list_response.json();

  //   if (stock_list_response.status !== 200) {
  //     throw Error(body.message);
  //   }

  //   return body;
  // };

  // fetching the GET route from the Express server which matches the GET route from server.js
  // const retrieveSentimentAnalysis = async () => {
  //   const sentiment_analysis_response = await fetch("/sentiment_analysis");
  //   const body = await sentiment_analysis_response.json();

  //   if (sentiment_analysis_response.status !== 200) {
  //     throw Error(body.message);
  //   }

  //   return body;
  // };

  // fetching the GET route from the Express server which matches the GET route from server.js
  // const retrieveTimeSeriesForcast = async () => {
  //   const time_series_forecast_response = await fetch("/time_series_forecast");
  //   const body = await time_series_forecast_response.json();

  //   if (time_series_forecast_response.status !== 200) {
  //     throw Error(body.message);
  //   }
  //   return body;
  // };

  // React.useEffect(() => {
  //   retrieveStockList()
  //     .then((res) => {
  //       setData(res.express);
  //       console.log(res.express);
  //       console.log(data);
  //     })
  //     .catch((err) => console.log(err));

  //   retrieveSentimentAnalysis()
  //     .then((res) => {
  //       setData(res.express);
  //       console.log(res.express);
  //     })
  //     .catch((err) => console.log(err));

  //   retrieveTimeSeriesForcast()
  //     .then((res) => {
  //       setData(res.express);
  //       console.log(res.express);
  //     })
  //     .catch((err) => console.log(err));
  // }, []);

  React.useEffect(() => {
    var script = document.createElement("script");
    script.innerHTML = watson_script;
    document.body.appendChild(script);
  }, []);

  const classes = useStyles();
  return (
    <div>
      <GridContainer>
        <GridItem xs={12} sm={6} md={3}>
          <Card>
            <CardHeader color="info" stats icon>
              <CardIcon color="info">
                <Icon>info_outline</Icon>
              </CardIcon>
              <p className={classes.cardCategory}>Apple</p>
              <h3 className={classes.cardTitle}>Business</h3>
            </CardHeader>
            <CardFooter stats>
              <div className={classes.stats}>
                <Update />
                <a href="#pablo" onClick={(e) => e.preventDefault()}>
                  Just Updated
                </a>
              </div>
            </CardFooter>
          </Card>
        </GridItem>
      </GridContainer>
     <GridContainer>
        <GridItem xs={12} sm={12} md={6}>
          <Card>
            <CardHeader color="info">
              <h4 className={classes.cardTitleWhite}>Super Size Me wins writers' award</h4>
            </CardHeader>
            <CardBody>
              <Table
                tableHeaderColor="info"
                tableData={[
                  [
                    "Super Size Me director Morgan Spurlock has won the Writers Guild of America's award for documentary feature writing. The Oscar-nominated film followed Spurlock as he ate only McDonald's fast food for an entire month. Spurlock was given the award at a special ceremony at the Roosevelt Hotel in Hollywood on Tuesday. Organisers said the rising popularity of documentary films led them to honour a writer for a documentary screenplay for the first time. Producer Brian Grazer presented the award to Spurlock and the film\'s backers, Roadside Attractions, Samuel Goldwyn Films and Showtime Independent Films. Spurlock set out to discover the effect of living on nothing but McDonald's for a month, upgrading to supersize portions when offered. The film followed his 25lb weight gain and the health effects on his body, including his liver and cholesterol levels. McDonald\'s announced it was to scrap its 'supersized' meals last year, but denied the move was as a result of the negative publicity created by Spurlock's film. Spurlock was given his award on the same day the European Court of Human Rights ruled that two UK activists should have been given legal aid in their long fight against a McDonald\'s libel action. Helen Steel and David Morris, from north London, dubbed the 'McLibel Two', were found guilty in a 1990s trial of libelling the company in a leaflet they had been handing out At the end of the case the High Court in London ruled McDonald\'s had been libelled and awarded the company \xc2\xa360,000 in damages, later reduced to \xc2\xa340,000 on appeal. But he found the leaflet was true in some aspects.\n'",
                  ],
                ]}
              />
            </CardBody>
          </Card>
        </GridItem>
      </GridContainer>
    </div>
  );
}
